mapper.select:
SELECT COUNT(*) FROM sm_material_move
WHERE 1=1

	AND pi_id LIKE CONCAT ('%', #{searchKeyword}, '%')


	 AND CAST(MM_REG_DATE AS DATE) >= #{searchStDate}


	 AND CAST(MM_REG_DATE AS DATE) <=
	#{searchEdDate}

SELECT mm_id, pi_id, mm_in, mm_out FROM sm_material_move WHERE 1=1
AND pi_id LIKE CONCAT ('%', #{searchKeyword}, '%')
AND CAST(MM_REG_DATE AS DATE) >= #{searchStDate}
AND CAST(MM_REG_DATE AS DATE) <= #{searchEdDate}
ORDER BY mm_id DESC OFFSET (#{firstIndex}) ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY
