mapper.select:
SELECT COUNT(*) FROM sm_equip_checksheet_info
WHERE 1=1

	AND pr_chk_dte >= #{searchStDate}


	AND pr_chk_dte <= #{searchEdDate}

SELECT a.eci_id, b.eq_id, a.eci_name, a.eci_comment, a.eci_manager, a.eci_date FROM sm_equip_checksheet_info a JOIN sm_equipment b ON a.eq_id = b.eq_id WHERE 1=1
AND pr_chk_dte >= #{searchStDate}
AND pr_chk_dte <= #{searchEdDate}
ORDER BY eci_id DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY
