mapper.select:
SELECT COUNT(*) FROM sm_ms_log
where 1=1

	AND ml_id = #{searchKeyword}

SELECT ml_idx, ml_id, ml_ip, CONVERT(varchar(16), ml_date, 120) AS ml_date, ml_comment FROM sm_ms_log where 1=1
AND ml_id = #{searchKeyword}
ORDER BY ml_idx DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY
