mapper.select:
SELECT COUNT(*) FROM sm_inspection_info a JOIN sm_standard_info b ON a.SI_id = b.si_id
WHERE 1=1
AND po_lotno LIKE CONCAT('%',#{searchKeyword},'%')
AND id_test_time >= #{searchStDate}
AND id_test_time <= #{searchEdDate}

SELECT isi_id,or_id,isi_item_name,isi_way,isi_date,isi_manager,isi_file1,isi_file2,isi_file3,isi_file4, isi_file5, b.si_name, a.si_id, a.isi_item_type, a.isi_spc_spec, a.isi_check,a.isi_file1 FROM sm_inspection_info a JOIN sm_standard_info b ON a.SI_id = b.si_id where 1=1
AND po_lotno LIKE CONCAT('%',#{searchKeyword},'%')
AND id_test_time >= #{searchStDate}
AND id_test_time <= #{searchEdDate}
ORDER BY isi_id DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY

SELECT id_doc FROM
sm_inspection_excel_history
WHERE ieh_lotno = #{isiLotno}

SELECT COUNT(*) FROM
sm_inspection_info
WHERE isi_lotno = #{isiLotno}

mapper.update:
