mapper.select:
select count(*) from sm_release_info 
where 1=1

	and po_lotno LIKE CONCAT ('%', #{searchKeyword}, '%')


	AND rel_date >= #{searchStDate}


	AND rel_date <=
	#{searchEdDate}

SELECT rel_idx,po_lotno, rel_date, rel_compony, rel_sub, rel_prod, rel_qty,rel_bill FROM sm_release_info WHERE 1=1
and po_lotno LIKE CONCAT ('%', #{searchKeyword}, '%')
AND rel_date >= #{searchStDate}
AND rel_date <= #{searchEdDate}
ORDER BY rel_idx DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY
