mapper.select:
SELECT si_id,
si_type, si_name FROM sm_standard_info
WHERE si_id = #{giGroupcode}

mapper.update:
UPDATE sm_quality_info SET
qi_type = #{qiType},
qi_isuse = #{qiIsuse}
,qi_comment = #{qiComment}
,qi_remark = #{qiRemark}
,qi_edt_id = #{userId},
qi_edt_date = getdate()
WHERE qi_code =
#{qiCode}
