mapper.select:
SELECT COUNT(*) FROM sm_inspection_data
WHERE 1=1

	AND po_lotno LIKE CONCAT('%',#{searchKeyword},'%')


	AND id_test_time >= #{searchStDate}


	AND id_test_time <=
	#{searchEdDate}

SELECT * from sm_inspection_data where 1=1
AND po_lotno LIKE CONCAT('%',#{searchKeyword},'%')
AND id_test_time >= #{searchStDate}
AND id_test_time <= #{searchEdDate}
ORDER BY id_id DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY

mapper.update:
UPDATE sm_item SET
	item_cnt = (item_cnt + #{itemCnt})
WHERE item_cd = #{itemCd}

UPDATE sm_shipment SET
	sh_state = #{state}
WHERE sh_idx = #{shIdx}

UPDATE sm_orders SET
	or_state = #{state}
WHERE or_idx = #{orIdx}
