mapper.select:
SELECT COUNT(*) FROM sm_kpi_info
WHERE 1=1

	AND ki_year = #{searchCondition}


	AND ki_month = #{searchCondition2}


	AND ki_type = #{searchCondition3}

SELECT ki_id, ki_year, ki_month, ki_type, ki_qty, ki_bad_qty, ki_good_qty FROM sm_kpi_info WHERE 1=1
AND ki_type = #{searchCondition}
AND ki_year = #{searchCondition2}
AND ki_month = #{searchCondition3}
ORDER BY ki_month DESC OFFSET #{firstIndex} ROWS FETCH NEXT #{recordCountPerPage} ROWS ONLY

SELECT
	ki_id,
	ki_year,
	ki_month,
	ki_qty
FROM sm_kpi_info
WHERE 1=1

	AND ki_type = #{searchCondition}


	AND ki_year = #{searchCondition2}


	AND ki_month = #{searchCondition3}

SELECT DATE_FORMAT(pr_re_reg_dte, '%Y-%m') monthly, DATE_FORMAT(pr_re_reg_dte, '%Y') years, DATE_FORMAT(pr_re_reg_dte, '%m') MONTH, SUM(pr_re_cnt) cnt, SUM(pr_re_faulty_cnt) faulty, (SUM(pr_re_cnt)-SUM(pr_re_faulty_cnt)) prodCnt, ROUND((SUM(pr_re_faulty_cnt)/SUM(pr_re_cnt)*100),2) percent, 0 money FROM sm_prod_result WHERE 1=1
AND DATE_FORMAT(pr_re_reg_dte, '%Y') = #{searchCondition2}
AND FORMAT(DATE_FORMAT(pr_re_reg_dte, '%m'),0) = #{searchCondition3}
AND pr_list_nm = (SELECT pr_list_nm FROM sm_process_list WHERE pr_list_seq = (SELECT MAX(pr_list_seq) FROM sm_process_list)) GROUP BY monthly

SELECT DATE_FORMAT(or_dte, '%Y-%m') monthly, DATE_FORMAT(or_dte, '%Y') years, DATE_FORMAT(or_dte, '%m') MONTH, ROUND(SUM(or_money/10000),1) money, 0 prodCnt, 0 percent FROM sm_orders WHERE 1=1
AND DATE_FORMAT(or_dte, '%Y') = #{searchCondition2}
AND FORMAT(DATE_FORMAT(or_dte, '%m'),0) = #{searchCondition3}
GROUP BY monthly

mapper.insert:
DECLARE @str NVARCHAR(50) = 'KI' DECLARE @seq INT DECLARE @padding INT = 5;
SET @seq = NEXT VALUE FOR ki_seq
INSERT INTO sm_kpi_info ( ki_id, ki_year, ki_month, ki_type, ki_qty, ki_good_qty, ki_bad_qty, ki_reg_id, ki_reg_date ) VALUES ( @str + RIGHT('00000' + CAST(@seq AS VARCHAR(10)), @padding), #{kiYear}, #{kiMonth}, #{kiType}, #{kiQty}, #{kiGoodQty}, #{kiBadQty}, #{userId}, GETDATE() )

mapper.update:
UPDATE sm_kpi_info SET
	ki_qty = #{kiQty},
	ki_good_qty = #{kiGoodQty},
	ki_bad_qty = #{kiBadQty}
WHERE ki_id = #{kiId}
