mapper.update:
update sm_product_order set
po_state = #{poState}
where po_lotno = #{poLotno}

mapper.select:
SELECT COUNT(*)
FROM
sm_product_order WHERE po_lotno = #{poLotno}

mapper.update:
UPDATE sm_cut_proc_log SET
cpl_manager = #{cplManager}
WHERE or_id = #{orId} AND cpl_idx = (
SELECT MAX(cpl_idx)
FROM sm_cut_proc_log
WHERE or_id = #{orId}
)
